import React from 'react'
import { useState } from 'react';
import 'bootswatch/dist/vapor/bootstrap.min.css';
import axios from 'axios'

function Login() {
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')

    function handleSubmit(event) {
        event.preventDefault();
        axios.post('http://localhost:8081/')
    }

    return (
        <div className='d-flex vh-100 justify-content-center align-items-center bg-primary'>
            <form onSubmit = {handleSubmit}>
                <div className='mb-3'>
                    <label for="inputEmail" className="form-label mt-4" style={{ color: 'white' }}>Email</label>
                    {/* When input changes, setEmail retrieves value and update email variable */}
                    <input type="email" className="form-control" id="inputEmail" aria-describedby="emailHelp" placeholder="Enter email" 
                    onChange={e => setEmail(e.target.value)}
                    />
                    <small id="emailHelp" style={{ color: 'white' }} >We'll never share your email with anyone else.</small>
                </div>
                <div className='mb-3'>
                    <label for="inputPassword" class="form-label mt-4" style={{ color: 'white' }}>Password</label>
                    {/* When input changes, setPassword retrieves value and update password variable */}

                    <input type="password" className="form-control" id="inputPassword" placeholder="Password" autocomplete="off" 
                    onChange={e => setPassword(e.target.value)} 
                    />

                </div>
                <button type="button" className="btn btn-secondary">Login</button>

            </form>
        </div>
    );
}


export default Login;
