import React from 'react'
import './Home.css'
import { useState } from 'react';
import validation from '../Components/SignUpValidation';
import axios from 'axios';

const Home = () => {

  const [MovieReg, setMovieTitle] = useState("");



    function handleSubmit(event) {
      event.preventDefault();
      setErrors(validation(values));
      {
          
          axios.post('http://localhost:3000/Home', 
          { 
              MovieTitle: MovieReg,
          })
              .then(res => console.log(res))
              .catch(err => console.log(err));
      }
  }

  return (
    <div class ="container">
        <div class = "search-container">
            <input type="search" id="SearchBar" placeholder='Search for Movie' className="Search" name='MovieReg'
                onChange={(e) => {setMovieTitle(e.target.value)}} />


          <button className="searchBtn" type="submit">
              Search
          </button>  
          {/*<script src="server.js"></script>*/}

        </div>
    </div>
  )
}

export default Home
