import './Home.css'
import validation from '../Components/SignUpValidation';
import axios from 'axios';
import React, {useEffect, useState} from 'react';

const Home = () => {
  useEffect( () => {
    fetchMovie();
  }, []);

  const [Movie, setMovie] = useState("");

  const fetchMovie = async () => {
    const data = await fetch('/Home');
    const Movie = await data.json();
    setMovie(Movie);
  }


  return (
    <div class ="container">
        <div class = "search-container">
            <input type="search" id="SearchBar" placeholder='Search for Movie' className="Search" name='MovieReg'/>


          <button className="searchBtn" type="submit">
              Search
          </button>  
          {/*<script src="server.js"></script>*/}

        </div>
      {
        Movie.map(item => (
          <div class="row padding">
            <div class="alert alert-info rounded-pill" role="alert">
                <i class="fa fa-user mr-2"></i> <i>{item.title} ({item.Airing}): {item.ReleaseDate}</i>
            </div>
          </div>        
        ))
      }
    

    </div>
  )
}

export default Home
