{/* Return Errors */}

function validation(values)  {
    let error = {}

    {/* Check constraints for valid email and one character for password*/}
    const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const passwordPattern = /^.{2,}$/;
    const FnamePattern = /^.{2,}$/;
    const LnamePattern = /^.{2,}$/;

    if (values.email === "") {
        error.email = "Email should not be empty"
    }
    else if (!emailPattern.test(values.email)) {
        error.email = "Email Didn't match"
    } else {
        error.email = ""
    }

    if (values.password === "") {
        error.password = "Password should not be empty"
    }
    else if (!passwordPattern.test(values.password)) {
        error.password = "Password didn't match"
    } else {
        error.password = ""
    }

    if (values.FName === "") {
        error.password = "Name should not be empty"
    }
    else if (!FnamePattern.test(values.FName)) {
        error.FName = "First Name didn't match"
    } else {
        error.FName = ""
    }

    if (values.LName === "") {
        error.password = "Name should not be empty"
    }
    else if (!LnamePattern.test(values.LName)) {
        error.LName = "Last Name didn't match"
    } else {
        error.LName = ""
    }

    return error;


}

export default validation;
